INSERT INTO `community_board` VALUES (1,6,'오랜만에 친구들과의 만남!','평소 연락만 하다가 오랜만에 친구들이랑 만나서 산책했어요 ㅎㅎ 절미가 너무 좋아하네요! 집에서와는 달리 너무 신나하네요~~~ 자주 약속잡아서 나와야겠네요~~~ 우주선 어린이 공원 갔다가 황솔촌도 들렸습니다!',1,35.19143466684090,126.81310216667800,'2024-02-15 02:14:58',56,5),(2,6,'절미도 쭈꾸미 도전~?','오늘 절미와는 아니지만 신쭈꾸미라는 곳을 가봤어요~ 장덕동 맛집이더라구요?? 너어무 맛있게 먹었네요 ㅎㅎ 그리고 강아지들도 쭈꾸미 먹어도 좋다고 하네요!! 다들 한번 가보시는 것 추천드립니다~~',1,35.19053085136940,126.81337598680700,'2024-02-15 02:50:59',28,3),(3,16,'쿠키 미용하고 왔어요','오랜만에 산책하고 미용했어요~~ 안그래도 동그란 쿠키 더 쿠키 같아졌네용 ㅎㅎ',1,35.18913746403990,126.81158114921500,'2024-02-15 23:57:24',3,5),(4,7,'렐리랑 첫 산책','렐리랑 첫 산책 나왔습니다! 다른 분들도 함께 산책하면 애기들 사회성 기르는데 좋을 것 같아요~ 저는 매일 10시 3시 8시에 장덕동 일대를 산책합니다!! ',1,35.19789947916502,126.81500614558007,'2024-02-15 23:58:33',2,5),(5,11,'절미 땅 파먹은 날','저희 절미 운동장 놀러갔다가 땅 속에... 간식냄새가 솔솔 났는지 코를 처박고 놀았습니다. 이런 댕청댕청한 모습마저 너무 귀여워서 죽을 것 같아요... 저희 강아지 귀여운거 다들 봐주세요~',1,35.18981270485880,126.81989947166706,'2024-02-16 00:01:39',3,2),(6,11,'장덕동 애견용품 할인점','간식 할인 행사 중입니다!!! 울 애기와 같이 들어갈 수 있어서 같이 쇼핑했어요~~ 간식 쟁여두실 분들은 서두르세요!!',1,35.19037630813050,126.81119125157515,'2024-02-16 00:03:26',1,4),(7,7,'슬개골 보호대 공유','저희 아이 슬개골 걱정돼서 수술 후 보호대 뭐가 좋은지 4개 정도 내돈내산 후 제일 좋은 것을 공유해드립니다! \"펫츠오앤피\" 강아지 슬개골 탈구 보호대 입니다!! 가격대가 있지만 우리 아이를 위해 하나 장만해보세요!',1,35.20140750815676,126.80465382839770,'2024-02-16 00:07:54',1,1);
